import React, { useState } from 'react';
import { View, TextInput, Button, Platform, StyleSheet, Text } from 'react-native';

import Tabs from "../Tabs";

const LoginScreen = () => {
  const [cedula, setCedula] = useState('');
  const [contrasenia, setContrasenia] = useState('');

  const handleLogin = () => {
    // Realizar la lógica de inicio de sesión aquí
    console.log('Cédula:', cedula);
    console.log('Contraseña:', contrasenia);
    navigation.navigate('Tabs');
  };

  const renderCedulaWarning = () => {
    if (cedula.length < 10) {
      return (
        <Text>
          La cedula tiene que tener 10 digitos.
        </Text>
      );
    }
    return null;
  };

  const renderContraseniaWarning = () =>{
    if(contrasenia.length<5){
        return(
            <Text>
                Ingrese una contraseña valida, minimo 4 caracteres
            </Text>
        )
    }
  }

  return (
    <View style={styles.container}>
        <Text style={styles.title}>INICIO SECION USUARIO FISEI</Text>
      <TextInput
        style={styles.input}
        placeholder="Cédula"
        placeholderTextColor="#000000"
        value={cedula}
        onChangeText={(text) => {
            const numericValue = text.replace(/[^0-9]/g, "");
            setCedula(numericValue);
          }}
        maxLength={10}
        keyboardType="numeric" // Permitir solo entrada numérica
      />      
        {renderCedulaWarning()}
      <TextInput
        style={styles.input}
        placeholder="Contraseña"
        secureTextEntry
        value={contrasenia}
        onChangeText={setContrasenia}
        maxLength={25}
      />
      {renderContraseniaWarning()}
      <Text></Text>
      <Button title="Iniciar sesión" onPress={handleLogin} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  }, 
   title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  input: {
    width: '100%',
    height: 40,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    marginBottom: 16,
    paddingHorizontal: 10,
  },
});

export default LoginScreen;
