import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import React from 'react';

const TasksItem = ({ persona }) => {
  return (
    <View style={styles.container}>
      <ScrollView horizontal={true}>
        <View style={styles.tableContainer}>
          <View style={styles.headerRow}>
            <Text style={styles.headerText}>Cédula</Text>
            <Text style={styles.headerText}>Nombre</Text>
            <Text style={styles.headerText}>Apellido</Text>
            <Text style={styles.headerText}>Teléfono</Text>
            <Text style={styles.headerText}>Correo</Text>
            <Text style={styles.headerText}>Dirección</Text>
            <Text style={styles.headerText}>Rol</Text>
            <Text style={styles.headerText}>Estado</Text>
          </View>
          <View style={styles.dataRow}>
            <View style={styles.rowContainer}>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.cedula_per}</Text>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.nombre_per}</Text>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.apellido_per}</Text>
            </View>
            <View style={styles.rowContainer}>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.telefono_per}</Text>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.correo_per}</Text>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.direccion_per}</Text>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.rol_per}</Text>
              <Text style={[styles.textContainer, styles.noWrap]}>{persona.estado}</Text>
            </View>
          </View>
        </View>
      </ScrollView>
      <View style={styles.optionContainer}>
        <TouchableOpacity>
          <Text>Delete</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  tableContainer: {
    paddingVertical: 20,
  },
  headerRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
    marginBottom: 10,
  },
  dataRow: {
    flexDirection: 'row',
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  rowContainer: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  headerText: {
    color: '#ffff',
    fontWeight: 'bold',
    width: 100,
  },
  textContainer: {
    color: '#ffff',
    width: 100,
  },
  noWrap: {
    whiteSpace: 'nowrap',
  },
  optionContainer: {
    flexDirection: 'row',
    position: 'absolute',
    justifyContent: 'center',
  },
});

export default TasksItem;
