import React, { useState } from 'react';
import { View, TextInput, Button, Platform, StyleSheet } from 'react-native';

const LoginScreen = () => {
  const [cedula, setCedula] = useState('');
  const [contrasenia, setContrasenia] = useState('');

  const handleLogin = () => {
    // Realizar la lógica de inicio de sesión aquí
    console.log('Cédula:', cedula);
    console.log('Contraseña:', contrasenia);
  };

  return (
    <View style={styles.container}>
      <TextInput
          style={styles.input}
          placeholder="Cedula Persona"
          placeholderTextColor="#000000"
          value={persona.cedula_per}
          onChangeText={(text) => {
            const numericValue = text.replace(/[^0-9]/g, "");
            setCedula(numericValue);
            handleChangePersona('cedula_per', numericValue);
          }}
          maxLength={10}
          keyboardType="numeric" // Permitir solo entrada numérica
          editable={route.params && route.params.cedula_per ? false : true} // Desactivar edición si es una actualización
        />
        {renderCedulaWarning()}
      <TextInput
        style={styles.input}
        placeholder="Contraseña"
        secureTextEntry
        value={contrasenia}
        onChangeText={setContrasenia}
        maxLength={25}
      />
      <Button title="Iniciar sesión" onPress={handleLogin} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  input: {
    width: '100%',
    height: 40,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    marginBottom: 16,
    paddingHorizontal: 10,
  },
});

export default LoginScreen;
