import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import React from 'react';

const TasksItem = ({ persona }) => {
  return (
    <ScrollView horizontal={true}>
      <ScrollView contentContainerStyle={styles.tableContainer} horizontal={true}>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Cédula</Text>
          <Text style={styles.textContainer}>{persona.cedula_per}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Nombre</Text>
          <Text style={styles.textContainer}>{persona.nombre_per}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Apellido</Text>
          <Text style={styles.textContainer}>{persona.apellido_per}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Teléfono</Text>
          <Text style={styles.textContainer}>{persona.telefono_per}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Correo</Text>
          <Text style={styles.textContainer}>{persona.correo_per}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Dirección</Text>
          <Text style={styles.textContainer}>{persona.direccion_per}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Rol</Text>
          <Text style={styles.textContainer}>{persona.rol_per}</Text>
        </View>
        <View style={styles.rowContainer}>
          <Text style={styles.staticTextContainer}>Estado</Text>
          <Text style={styles.textContainer}>{persona.estado}</Text>
        </View>
      </ScrollView>
      <View style={styles.optionContainer}>
        <TouchableOpacity>
          <Text>Delete</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  tableContainer: {
    flexGrow: 1,
    paddingVertical: 20,
  },
  rowContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  staticTextContainer: {
    color: '#ffff',
    width: 100,
  },
  textContainer: {
    color: '#ffff',
  },
  optionContainer: {
    flexDirection: 'row',
    position: 'absolute',
    justifyContent: 'center',
  },
});

export default TasksItem;
