import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import React from 'react';

const TasksItem = ({ persona }) => {
  return (
    <ScrollView horizontal={true}>
      <View style={styles.itemContainer}>
        <View style={styles.rowContainer}>
          <View style={[styles.cell, styles.headerCell]}>
            <Text style={styles.headerText}>CÃ©dula</Text>
          </View>
          <View style={[styles.cell, styles.headerCell]}>
            <Text style={styles.headerText}>Nombre</Text>
          </View>
          <View style={[styles.cell, styles.headerCell]}>
            <Text style={styles.headerText}>Apellido</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.cedula_per}</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.nombre_per}</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.apellido_per}</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.telefono_per}</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.correo_per}</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.direccion_per}</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.rol_per}</Text>
          </View>
          <View style={[styles.cell, styles.dataCell]}>
            <Text style={styles.textContainer}>{persona.estado}</Text>
          </View>
        </View>
        <View style={styles.optionContainer}>
          <TouchableOpacity>
            <Text>Delete</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  itemContainer: {
    backgroundColor: '#333333',
    padding: 20,
    marginVertical: 28,
    borderRadius: 25,
  },
  rowContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
  },
  cell: {
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 1,
    borderColor: '#ffffff',
    padding: 5,
    margin: 2,
  },
  headerCell: {
    flex: 1,
    backgroundColor: '#cccccc',
  },
  dataCell: {
    flex: 1,
    backgroundColor: '#ffffff',
  },
  headerText: {
    color: '#000000',
    fontWeight: 'bold',
  },
  textContainer: {
    color: '#ffffff',
  },
  optionContainer: {
    flexDirection: 'row',
    position: 'absolute',
    justifyContent: 'center',
    marginTop: 10,
  },
});

export default TasksItem;
