import React, { useState } from 'react';
import { View, TextInput, Button, Platform, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';

import { login } from "../api"; // Importa la función para autenticar al usuario

const LoginScreen = () => {
  const [cedula, setCedula] = useState('');
  const [contrasenia, setContrasenia] = useState('');
  const [error, setError] = useState(null);
  const navigation = useNavigation();

  const handleLogin = async () => {
    try {
      const user = await login(cedula, contrasenia); // Llama a la función de autenticación con los datos del usuario
      if (user) {
        navigation.navigate('HomeScreen'); // Redirige a la pantalla "PersonaScreen" si la autenticación es exitosa
      } else {
        setError('Credenciales inválidas'); // Muestra un mensaje de error si las credenciales son inválidas
      }
    } catch (error) {
      console.log('Error al autenticar al usuario:', error);
      setError('Ocurrió un error al iniciar sesión'); // Muestra un mensaje de error genérico si hay un error en la autenticación
    }
  };

  const renderCedulaWarning = () => {
    if (cedula.length < 10) {
      return (
        <Text>
          La cedula tiene que tener 10 digitos.
        </Text>
      );
    }
    return null;
  };

  const renderContraseniaWarning = () => {
    if (contrasenia.length < 5) {
      return (
        <Text>
          Ingrese una contraseña válida, mínimo 5 caracteres.
        </Text>
      );
    }
    return null;
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>INICIO SESIÓN USUARIO FISEI</Text>
      <TextInput
        style={styles.input}
        placeholder="Cédula"
        placeholderTextColor="#000000"
        value={cedula}
        onChangeText={(text) => {
          const numericValue = text.replace(/[^0-9]/g, "");
          setCedula(numericValue);
        }}
        maxLength={10}
        keyboardType="numeric" // Permitir solo entrada numérica
      />
      {renderCedulaWarning()}
      <TextInput
        style={styles.input}
        placeholder="Contraseña"
        secureTextEntry
        value={contrasenia}
        onChangeText={setContrasenia}
        maxLength={25}
      />
      {renderContraseniaWarning()}
      {error && <Text style={styles.error}>{error}</Text>} {/* Renderiza el mensaje de error si existe */}
      <Button title="Iniciar sesión" onPress={handleLogin} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  input: {
    width: '100%',
    height: 40,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    marginBottom: 16,
    paddingHorizontal: 10,
  },
  error: {
    color: 'red',
    marginBottom: 16,
  },
});

export default LoginScreen;
